import 'dart:async';

import 'package:flutter/material.dart';
import 'package:shared_preferences/shared_preferences.dart';
import 'package:umer/User/View/bottombar.dart';
import 'package:umer/User/View/fetch_screen.dart';
import 'package:umer/User/View/user_login.dart';
import 'package:umer/User/View/user_register.dart';

class Splash_Screen extends StatefulWidget {
  const Splash_Screen({super.key});

  @override
  State<Splash_Screen> createState() => _Splash_ScreenState();
}

class _Splash_ScreenState extends State<Splash_Screen> {

  void screenShifter()async{
    SharedPreferences userCred = await SharedPreferences.getInstance();
   var usersession=  userCred.getString("email");
   if(usersession != null){
     Timer(Duration(milliseconds: 2000), () => Navigator.push(context, MaterialPageRoute(builder: (context) => Bottom_bar(),)),);

   }else{
     Timer(Duration(milliseconds: 2000), () => Navigator.push(context, MaterialPageRoute(builder: (context) => User_Login(),)),);

   }
  }

  @override
  void initState() {
    // TODO: implement initState
 screenShifter();
    super.initState();
  }
  
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: Container(
        height: 700,
        width: double.infinity,
        decoration: BoxDecoration(
          image: DecorationImage(
              fit: BoxFit.cover,
              image: NetworkImage("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQRgffrQ_AKygrScV-mL2myGLE1eD_6tMG3Hw&s"))
        ),
        child: Center(
            child: CircleAvatar(
              radius: 100,

              backgroundImage: NetworkImage("data:image/png;base64,"),
            ),
          ),
      ),
    );
  }
}
